important;}\\nbody.wp-night-mode-on .header-brand * {\\n    background: none !important;\\n}\\n\\n.wpnm-button.style-1{\\ntop:5px;\\t\\n}\\n.wpnm-button.style-1.active .wpnm-slider {\\n\\t\\n    background-color: green !important;\\n}\\n\\n@media only screen and (max-width: 768px){\\n.secondary-toggle-bar {\\n    width: 23px;\\n}\\n\\t.clock #date{color:black;}\\n\\t.secondary-menu-toggle {\\n    top: 2px;\\n    right: 0px;\\n    width: 100%;\\n    height: 45px;\\n    display: block;\\n    float: left;\\n    position: relative;\\n}\\n\\t\\n}\",\n        \"type\": \"custom_css\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-12-26 16:36:40\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', '58049f87-3b87-44ed-a0fd-fa2bd6cd4f61', '', '', '2019-12-26 16:36:40', '2019-12-26 16:36:40', '', 0, 'http://gigawebsoft.com/news/2019/12/26/58049f87-3b87-44ed-a0fd-fa2bd6cd4f61/', 0, 'customize_changeset', '', 0),
(370, 1, '2019-12-26 16:36:40', '2019-12-26 16:36:40', '#sticky-wrapper{display:none;}\n#nav-sticker{top:0px !important;}\n.header-media-search{display:none;}\n#nav-sticker {\n	background-color: darkgreen;}\n.site-footer #colophon .site-info{display:none;}\n#nav-sticker-sticky-wrapper{display:none;}\n#secondary-menu-toggle{width:100%; display:block;}\n.menu-toggle{display:none;}\n.top-header{display:none;}\n\nbody.wp-night-mode-on .secondary-navigation ul li a{\n	color: #fff;\n}\nbody.wp-night-mode-on .secondary-navigation{\n	background: #393939;\n}\nbody.wp-night-mode-on .secondary-menu > li{\n	border:none;\n}\n.custom-logo-link img{\n	height:200px;\n}\n\nbody.wp-night-mode-on .pagination .nav-links,body.wp-night-mode-on .post-navigation .nav-links .nav-next,body.wp-night-mode-on .post-navigation .nav-links .nav-previous {\n	background:#1f1f1f;\n}\nbody.wp-night-mode-on #primary-menu, body.wp-night-mode-on #primary-menu *{ \n	background:#393939;\n}\nbody.wp-night-mode-on .entry-content-holder, body.wp-night-mode-on .entry-content-holder .entry-header,body.wp-night-mode-on .entry-content-holder .entry-content,body.wp-night-mode-on .entry-content-holder .entry-content *,body.wp-night-mode-on .entry-content-holder .entry-footer,body.wp-night-mode-on .entry-content-holder .entry-footer *,body.wp-night-mode-on .entry-content-holder .entry-meta, body.wp-night-mode-on .entry-content-holder .entry-title,body.wp-night-mode-on .entry-content-holder .entry-title *,body.wp-night-mode-on .entry-content-holder .entry-meta span,body.wp-night-mode-on .entry-content-holder .entry-meta span.author a,body.wp-night-mode-on .entry-content-holder .entry-meta span.posted-on * {\n	background-color:#393939;\n}\n\n\n.secondary-menu > li:last-child {\n	float: right;\n	border:none;\n	margin-right: 5px;\n    margin-top: 5px;\n}\nbody.wp-night-mode-on .cat-links a{color:#fff !important;}\nbody.wp-night-mode-on .header-brand * {\n    background: none !important;\n}\n\n.wpnm-button.style-1{\ntop:5px;	\n}\n.wpnm-button.style-1.active .wpnm-slider {\n	\n    background-color: green !important;\n}\n\n@media only screen and (max-width: 768px){\n.secondary-toggle-bar {\n    width: 23px;\n}\n	.clock #date{color:black;}\n	.secondary-menu-toggle {\n    top: 2px;\n    right: 0px;\n    width: 100%;\n    height: 45px;\n    display: block;\n    float: left;\n    position: relative;\n}\n	\n}', 'freenews', '', 'inherit', 'closed', 'closed', '', '171-revision-v1', '', '', '2019-12-26 16:36:40', '2019-12-26 16:36:40', '', 171, 'http://gigawebsoft.com/news/2019/12/26/171-revision-v1/', 0, 'revision', '', 0),
